package com.example.listadinamicaheroes

import android.content.Context
import androidx.appcompat.app.AppCompatActivity
import android.os.Bundle
import android.view.LayoutInflater
import android.widget.EditText
import android.widget.Toast
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView
import com.example.listadinamicaheroes.databinding.ActivityMainBinding
import com.google.android.material.dialog.MaterialAlertDialogBuilder
import com.google.android.material.snackbar.Snackbar

class MainActivity : AppCompatActivity(), onClickListener {

    private lateinit var heroeAdapter: HeroeAdapter
    private lateinit var linearLayoutManager: RecyclerView.LayoutManager
    private lateinit var viewBinding: ActivityMainBinding

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        viewBinding = ActivityMainBinding.inflate(layoutInflater)
        setContentView(viewBinding.root)

        val preferences = getPreferences(Context.MODE_PRIVATE)
        val isFirstTime = preferences.getBoolean(getString(R.string.spFirstTime), true)

        if(isFirstTime){
            val dialogView = layoutInflater.inflate(R.layout.dialog_register,null)
            MaterialAlertDialogBuilder(this)
                .setView(dialogView)
                .setCancelable(false)
                .setPositiveButton(R.string.confirm_dialog) { dialogInteface, i_->
                    val userName = dialogView.findViewById<EditText>(R.id.etUserName).text.toString()
                    with(preferences.edit()){
                        putBoolean(getString(R.string.spFirstTime), false)
                        putString(getString(R.string.spUserName), userName)
                            .apply()
                    }
                    Toast.makeText(this, "Se ha registrado exitosamente", Toast.LENGTH_SHORT).show()
                }
                .show()
        }else{
            val userName = preferences.getString(getString(R.string.spUserName), getString(R.string.userDefaul))
            Toast.makeText(this, "Bienvenido $userName", Toast.LENGTH_SHORT).show()
        }

        heroeAdapter = HeroeAdapter(getHeroes(), this)
        linearLayoutManager = LinearLayoutManager(this)

        viewBinding.rvListaHeroes.apply {
            layoutManager = linearLayoutManager
            adapter = heroeAdapter
        }
    }

    private fun getHeroes():MutableList<Heroe>{
        val heroes = mutableListOf<Heroe>()

        val ironMan = Heroe(name = "Iron Man", alterEgo = "Tony Star", url = "data:image/jpeg;base64,")
        heroes.add(ironMan)
        val blackWidow = Heroe(name = "Black Widow", alterEgo = "Natacha Romanoff", url = "data:image/jpeg;base64,")
        heroes.add(blackWidow)
        val scarletWitch = Heroe(name = "Scarlet Witch", alterEgo = "Wanda Maximoff", url = "data:image/jpeg;base64,")
        heroes.add(scarletWitch)
        val capiAmerica = Heroe(name = "Capitan America", alterEgo = "Steve Rogers", url = "data:image/jpeg;base64,")
        heroes.add(capiAmerica)

        heroes.add(ironMan)
        heroes.add(blackWidow)
        heroes.add(scarletWitch)
        heroes.add(capiAmerica)
        heroes.add(ironMan)
        heroes.add(blackWidow)
        heroes.add(scarletWitch)
        heroes.add(capiAmerica)
        heroes.add(ironMan)
        heroes.add(blackWidow)
        heroes.add(scarletWitch)
        heroes.add(capiAmerica)
        heroes.add(ironMan)
        heroes.add(blackWidow)
        heroes.add(scarletWitch)
        heroes.add(capiAmerica)

        return heroes
    }

    override fun onClick(heroe: Heroe) {
        Toast.makeText(this, heroe.getFullName(), Toast.LENGTH_SHORT).show()
    }
}